package trebuchet

import java.io.File

class App {

    fun whatFloor(str: String): Int {
        if (str == "") {
            return 0
        }
        var floor: Int = 0
        for (chr in str) {
            when (chr) {
                '(' -> floor += 1
                ')' -> floor -= 1
            }
        }
        return floor
    }

    fun findPosition(str: String): Int {
        if (str == "") {
            return 0
        }
        var position: Int = 1
        var floor: Int = 0
        for (chr: Char  in str) {
            if (chr == '(' ) {
                floor += 1
            } else {
                floor -= 1
            }
            if (floor == -1) {
                return position
            }
            position += 1
        }
        return position
    }

    fun getCalibrationValuesOfDocument(fileName: String): Int {
        val currentDir = System.getProperty("user.dir")
        val file: File = File("$currentDir/src/main/resources/$fileName")
        var sumUp: Int = 0
        file.forEachLine {
            var numberOfNumbers: Int = 0
            var firstDigit: Char = ' '
            var secondDigit: Char = ' ' 
            for (chr: Char in it) {
                if (chr.isDigit() && numberOfNumbers == 0) {
                    firstDigit = chr
                    numberOfNumbers += 1
                } else if (chr.isDigit()) {
                    secondDigit = chr
                    numberOfNumbers += 1
                }
            }
            var finalNumber: String
            if (numberOfNumbers > 1) {
                finalNumber = firstDigit.toString() + secondDigit.toString()
            } else {
                finalNumber = firstDigit.toString() + firstDigit.toString()
            }
            sumUp += finalNumber.toInt()
        }
        return sumUp
    }

    fun returnNumber(str: String): String {
        var s: String
        when (str) {
            "one" -> s = "1"
            "two" -> s = "2"
            "three" -> s = "3"
            "four" -> s = "4"
            "five" -> s = "5"
            "six" -> s = "6"
            "seven" -> s = "7"
            "eight" -> s = "8"
            "nine" -> s = "9"
            else -> s = str
        }
        return s
    }

    fun getNewCalibrationValuesOfDocument(fileName: String): Int {
        val currentDir = System.getProperty("user.dir")
        val file: File = File("$currentDir/src/main/resources/$fileName")
        val regularExpression: String = """(?=([1-9]|one|two|three|four|five|six|seven|eight|nine))"""
        var sumUp: Int = 0
        file.forEachLine {
            val text = it
            val regex = regularExpression.toRegex()
            val matches = regex.findAll(text)
            val list = matches.map { it.groupValues[1] }.joinToString()
            val numbers = list.split(",")
            val firstNumber = returnNumber(numbers.first().replace(" ", ""))
            val lastNumber = returnNumber(numbers.last().replace(" ", ""))
            val n: Int = (firstNumber + lastNumber).toInt()
            sumUp += n
        }
        return sumUp
    }
}

